import React, { useEffect, useRef, useState } from "react";

const GameController = () => {
  const [displayedData, setDisplayedData] = useState([]);
  const [loading, setLoading] = useState(true);
  const randomCountryRef = useRef(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const result = await fetch();
        setDisplayedData(result);
      } catch (error) {
        console.error("Error fetching data:", error);
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  useEffect(() => {
    if (displayedData.length > 0) {
      randomCountryRef.current = [];
      const entry = getRandomInt(0, displayedData.length);
      const entry2 = getRandomInt(0, displayedData.length);
      randomCountryRef.current = [displayedData[entry], displayedData[entry2]];
      console.log(randomCountryRef.current);
      setLoading(false);
    }
  }, [displayedData]);

  const getRandomInt = (min, max) => {
    min = Math.ceil(min);
    max = Math.floor(max);
    return Math.floor(Math.random() * (max - min)) + min;
  };
  return (
    <div className="flex w-4/6 justify-center items-center  bg-gradient-to-b from-bgr1  to-bgr2">
      <div className="grid grid-cols-2 gap-4">
        {loading ? (
          <p>Loading...</p>
        ) : (
          <div className="flex flex-row gap-12">
            <div className="flex flex-col">
              <img
                className="w-96 h-96 object-cover"
                src={randomCountryRef.current[0].flags.png}
                alt={randomCountryRef.current[0].flags.alt}
              />
            </div>
            <div className="flex flex-col">
              <img
                className="w-96 h-96 object-cover"
                src={randomCountryRef.current[1].flags.png}
                alt={randomCountryRef.current[1].flags.alt}
              />
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default GameController;
