import React from "react";
import { useSelector } from "react-redux";

const checkAnswer = (countries, question, answer) => {
  let result = countries[0];
  switch (question.questionId) {
    case 0:
      for (let i = 0; i < countries.length; i++) {
        if (result.population < countries[i].population) {
          result = countries[i];
        }
      }
      console.log(result.name.common);
      console.log(answer.name.common);
      break;
  }
};

const CountryBtn = (props) => {
  const countries = useSelector((state) => state.country);
  const question = useSelector((state) => state.question);
  const { country } = props;

  return (
    <div
      className="flex flex-col items-center mt-5 cursor-pointer"
      onClick={() => checkAnswer(countries, question, country)}
    >
      <div className="bg-btn text-white h-20 flex items-center justify-center rounded-xl text-center font-bold w-full hover:bg-blue-700 transition duration-300 transform hover:scale-105 shadow-md">
        {country.name.common}
      </div>
    </div>
  );
};

export default CountryBtn;
