const initialState = {
  country: null,
};

const countryReducer = (state = initialState, action) => {
  switch (action.type) {
    case "SET_QUESTION":
      return {
        ...state,
        country: action.payload,
      };
    default:
      return state;
  }
};

export default countryReducer;
