import React from "react";
import { useSelector } from "react-redux";

const checkAnswer = (countries, question, answer) => {
  let result = countries[0];
  switch (question.questionId) {
    case 0:
      for (let i = 0; i < countries.length; i++) {
        if (result.population < countries[i].population) {
          result = countries[i];
        }
      }
      console.log(result.name.common);
      break;
  }
};

const CountryBtn = (props) => {
  const countries = useSelector((state) => state.country);
  const question = useSelector((state) => state.question);
  console.log(countries, question);
  const { country } = props;
  //console.log(country);

  return (
    <div
    className="flex flex-col items-center pt-5 pl-5 pr-5 mt-5 cursor-pointer"
    onClick={() => checkAnswer(countries, question, country)} // Passe le pays sélectionné à la fonction checkAnswer
  >
    {/* Conteneur du bouton de réponse (sans image, juste le nom du pays) */}
    <div className="bg-btn text-white p-4 rounded-xl text-center mt-5 font-bold w-full hover:bg-blue-700 transition duration-300">
      {country.name.common} {/* Affichage du nom du pays comme réponse */}
    </div>
  </div>
  );
};

export default CountryBtn;
