import React, { useState, useEffect, useRef } from "react";
import { fetch } from "../utils/fetch";
import GameController from "../components/gameController";

const HomePage = () => {
  const [displayedData, setDisplayedData] = useState([]);
  const [loading, setLoading] = useState(true);
  const randomCountryRef = useRef(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const result = await fetch();
        setDisplayedData(result);
      } catch (error) {
        console.error("Error fetching data:", error);
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  useEffect(() => {
    if (displayedData.length > 0) {
      randomCountryRef.current = [];
      const entry = getRandomInt(0, displayedData.length);
      const entry2 = getRandomInt(0, displayedData.length);
      randomCountryRef.current = [displayedData[entry], displayedData[entry2]];
      console.log(randomCountryRef.current);
      setLoading(false);
    }
  }, [displayedData]);

  const getRandomInt = (min, max) => {
    min = Math.ceil(min);
    max = Math.floor(max);
    return Math.floor(Math.random() * (max - min)) + min;
  };

  return (
    <div className="min-h-screen flex flex-col items-center">
      <div className="flex flex-col">
        <GameController />
      </div>
      {/*{loading ? (
