import React, { useState, useEffect } from "react";
import { fetch } from "../utils/fetch";
import CountryCard from "../components/countryCard";
import Filter from "../components/filter";

const HomePage = () => {
  const [randomCountry, setRandomCountry] = useState(null);
  const [displayedData, setDisplayedData] = useState([]);
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [executed, setExecuted] = useState(false);

  const getRandomInt = (min, max) => {
    min = Math.ceil(min);
    max = Math.floor(max);
    return Math.floor(Math.random() * (max - min)) + min;
  };

  useEffect(() => {
    const fetchData = async () => {
      try {
        const result = await fetch();
        console.log(result);
        setData(result);
        setDisplayedData(result); // Initially, display all data
        setLoading(false);
      } catch (error) {
        console.error("Error fetching data:", error);
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  useEffect(() => {
    if (data && !executed) {
      const entry = getRandomInt(0, 250);
      setRandomCountry(displayedData[entry]);
      setExecuted(true); // Set executed to true to prevent further executions
      console.log(randomCountry);
    }
  }, [data, displayedData, executed]);

  return (
    <div className="h-96">
      {loading ? <p>Loading...</p> : <div className=""></div>}
    </div>
  );
};

export default HomePage;
