import React, { useState, useEffect, useRef } from "react";
import { fetch } from "../utils/fetch";
import CountryCard from "../components/countryCard";
import Filter from "../components/filter";

const HomePage = () => {
  const [displayedData, setDisplayedData] = useState([]);
  const [loading, setLoading] = useState(true);
  const randomCountryRef = useRef(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const result = await fetch();
        setDisplayedData(result);
      } catch (error) {
        console.error("Error fetching data:", error);
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  useEffect(() => {
    if (displayedData.length > 0) {
      randomCountryRef.current = [];
      const entry = getRandomInt(0, displayedData.length);
      const entry2 = getRandomInt(0, displayedData.length);
      randomCountryRef.current = [];
      randomCountryRef.current.push(
        displayedData[entry],
        displayedData[entry2]
      );
      console.log(randomCountryRef.current);
      setLoading(false);
    }
  }, [displayedData]);

  const getRandomInt = (min, max) => {
    min = Math.ceil(min);
    max = Math.floor(max);
    return Math.floor(Math.random() * (max - min)) + min;
  };

  return (
    <div className="h-auto">
      {loading ? (
        <p>Loading...</p>
      ) : (
        <div className="flex flex-row  gap-12">
          <div className="flex flex-col">
            <img
              className="w-92 h-92"
              src={randomCountryRef.current[0].flags.png}
              alt={randomCountryRef.current[0].flags.alt}
            />
          </div>
          <div className="flex flex-col">
            <img
              className="w-92 h-92"
              src={randomCountryRef.current[1].flags.png}
              alt={randomCountryRef.current[1].flags.alt}
            />
          </div>
        </div>
      )}
    </div>
  );
};

export default HomePage;
