import React, { useState, useEffect, useRef } from "react";
import { fetch } from "../utils/fetch";
import CountryCard from "../components/countryCard";
import Filter from "../components/filter";

const HomePage = () => {
  const [displayedData, setDisplayedData] = useState([]);
  const [loading, setLoading] = useState(true);
  const randomCountryRef = useRef(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const result = await fetch();
        setDisplayedData(result);
        setLoading(false);
      } catch (error) {
        console.error("Error fetching data:", error);
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  useEffect(() => {
    if (displayedData.length > 0 && randomCountryRef.current === null) {
      const entry = getRandomInt(0, displayedData.length);
      randomCountryRef.current = displayedData[entry];
    }
  }, [displayedData]);

  const getRandomInt = (min, max) => {
    min = Math.ceil(min);
    max = Math.floor(max);
    return Math.floor(Math.random() * (max - min)) + min;
  };

  return (
    <div className="h-96">
      {loading ? (
        <p>Loading...</p>
      ) : (
        <CountryCard country={randomCountryRef.current} />
      )}
    </div>
  );
};

export default HomePage;
