import React, { useState, useEffect } from "react";
import { fetch } from "../utils/fetch";
import CountryCard from "../components/countryCard";
import Filter from "../components/filter";

const HomePage = () => {
  const [parentFilter, setParentFilter] = useState("all");
  const [displayedData, setDisplayedData] = useState([]);
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(true);

  const handleFilterChange = (newFilter) => {
    setParentFilter(newFilter);
  };

  useEffect(() => {
    const fetchData = async () => {
      try {
        const result = await fetch();
        console.log(result);
        setData(result);
        setDisplayedData(result); // Initially, display all data
      } catch (error) {
        console.error("Error fetching data:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  useEffect(() => {
    if (data) {
      let filteredData;
      if (parentFilter !== "all") {
        filteredData = data.filter((item) => item.region === parentFilter);
      } else {
        filteredData = [...data];
      }

      filteredData.sort((a, b) => a.name.common.localeCompare(b.name.common));

      setDisplayedData(filteredData);
    }
  }, [data, parentFilter]);

  return (
    <div className="h-96">
      <Filter onFilterChange={handleFilterChange} />
      {loading ? <p>Loading...</p> : <div className=""></div>}
    </div>
  );
};

export default HomePage;
